// This is our attempt to control load order and intialization of omniture
// for use in our apps.
angular.module('xdc-analytics')
.run(function($window, $log, dataBootstrap, xdcAnalytics) {
    try {
        var configAnalytics = dataBootstrap.get('config.analytics');
        var sAccount = configAnalytics.omniture.account;

        // We want to fail if configAnalytics doesn't exist.
        $window.visitor = $window.Visitor.getInstance(configAnalytics.omniture.mc_id, {
            trackingServer: configAnalytics.omniture.s.trackingServer,
            trackingServerSecure: configAnalytics.omniture.s.trackingServerSecure,

            // To enable CNAME support, add the following configuration variables
            // If you are not using CNAME, DO NOT include these variables
            marketingCloudServer: configAnalytics.omniture.s.trackingServer,
            marketingCloudServerSecure: configAnalytics.omniture.s.trackingServerSecure
        });

        // NOTE: The ordering here is very important because of the way Omniture expects the window.s object to look like
        $window.s = angular.extend($window.s_gi(sAccount), $window.s);

        // With visitor API Only
        $window.s.visitor = $window.Visitor.getInstance(configAnalytics.omniture.mc_id);
    } catch(err) {
        $log.warn('ADC.config is not defined in a way to support tracking: ' + err);
    }

    try {
        if (!dataBootstrap.get('model.params.has_custom_tracking')) {
            xdcAnalytics.init();
        }
    } catch(e) {
        // pass, no errors.
    }
});

