angular.module('fe-create-account', ['ngSanitize', 'ui.mask', 'fe-user', 'fe-shared-constants', 'fe-event-dispatch'])
.directive('feCreateAccount', function($http, $sce, $templateCache, feUserSvc, SHARED_CONSTANTS,
            environmentSvc, EventDispatchingSvc, xdcAnalytics) {
    return {
        restrict: 'EA',
        scope: {
            email: '@email'
        },
        transclude: true,
        templateUrl: 'fe-shared-components/templates/user/fe_create_account.tpl.html',

        link: function (scope, element, attrs, ctrl, transclude) {
            angular.extend(scope, new EventDispatchingSvc(scope));

            scope.initState = function() {
                scope.formSubmitted = false;
                scope.disableSubmitButton = false;
                scope.existingAccountError = false;
                scope.unconfirmedAccountError = false;
                scope.accountCreationInitiated = false;
                scope.isInvalidPhoneNumber = false;
                scope.str_disclaimer = $sce.trustAsHtml(
                    SHARED_CONSTANTS.CREATE_ACCOUNT_DISCLAIMER.str.replace(
                        '%(touurl)',
                        environmentSvc.isCre() ?
                            SHARED_CONSTANTS.TERMS_OF_USE.url
                            : SHARED_CONSTANTS.ENTITIES.CRE.url+SHARED_CONSTANTS.TERMS_OF_USE.url

                    ).replace(
                        '%(privacystmturl)',
                        environmentSvc.isCre() ?
                            SHARED_CONSTANTS.PRIVACY_STATEMENT.url
                            : SHARED_CONSTANTS.ENTITIES.CRE.url+SHARED_CONSTANTS.PRIVACY_STATEMENT.url

                    ).replace(
                        '%(entity)',
                        environmentSvc.isCre() ?
                        SHARED_CONSTANTS.ENTITIES.CRE.shorthand
                        : SHARED_CONSTANTS.ENTITIES.ADC.shorthand
                    )
                );
                scope.str_existing_account = $sce.trustAsHtml(
                    SHARED_CONSTANTS.CREATE_ACCOUNT_ALREADY_EXISTING.str
                    .replace(
                        '%(loginurl)',
                        environmentSvc.normalizeUrlByDomain(SHARED_CONSTANTS.LOGIN)
                    )
                );

                scope.cacField = {};
                scope.cacField.email = scope.email ? scope.email : '';
                scope.cacField.emailOptIn = true;
            };

            scope.isValidPhoneNumber = function() {
                if ((!scope.cacField.phone || scope.cacField.phone.match(/\d/g).length===10)) {
                    scope.isInvalidPhoneNumber = false;
                    return true;
                } else {
                    scope.isInvalidPhoneNumber = true;
                    return false;
                }
            };

            scope.isValidEmail = function() {
                if (scope.formSubmitted && scope.cacField.email &&
                            scope.cacField.email.match(/^[a-z0-9][a-z0-9_\.\-]*@[a-z0-9\.\-]+\.[a-z]{2,4}$/i)) {
                    scope.cacForm.email.$invalid = false;
                    return true;
                } else {
                    scope.cacForm.email.$invalid = true;
                    return false;
                }
            };

            scope.submit = function() {
                scope.formSubmitted = true;
                scope.dispatch('contrib:caSubmitted');
                if (scope.isValidEmail() && scope.isValidPhoneNumber() && scope.cacForm.$valid) {
                    if (scope.cacField.phone) {
                        scope.cacField.phone = scope.cacField.phone.replace(/\D/g, '')
                                .replace(/(\d{3})(\d{3})(\d{4})/, '$1-$2-$3');
                    }

                    scope.disableSubmitButton = true;
                    feUserSvc.createAccount(scope.cacField)
                        .then(function(response) {
                            if (response.data.result === 'success') {
                                xdcAnalytics.trackAccountCreateSuccess();
                                scope.accountCreationInitiated = true;
                                scope.dispatch('contrib:caSucc');
                            } else {
                                xdcAnalytics.trackAccountCreateFail();
                                scope.accountCreationInitiated = false;
                                scope.errorEmail = scope.cacField.email;
                                var ERR_US112 = 'US112', // account exists on the same domain
                                    ERR_US112_1 = 'US112_1', // signup on ten-x but account exists on adc
                                    ERR_US112_2 = 'US112_2', // signup on adc but account exists on ten-x
                                    ERR_US113 = 'US113', // account confirmation pending
                                    currentError;
                                if (response.data.error.code === ERR_US112) {
                                    currentError = ERR_US112;
                                } else if (response.data.error.code === ERR_US112_1) {
                                    currentError = ERR_US112_1;
                                } else if (response.data.error.code === ERR_US112_2) {
                                    currentError = ERR_US112_2;
                                } else if (response.data.error.code === ERR_US113) {
                                    currentError = ERR_US113;
                                }
                                scope.dispatch('contrib:caErr', {
                                    'error': currentError,
                                    'email': scope.errorEmail
                                });

                                scope.disableSubmitButton = false;
                            }
                        });
                }
            };

            scope.initState();
        }
    };
})
// Allow input to accept only letters
.directive('lettersOnly', function ($parse) {
    return {
        require: 'ngModel',
        link: function (scope, element, attrs, modelCtrl) {
            modelCtrl.$parsers.push(function (inputValue) {
                if (inputValue === undefined) {
                    return '';
                }
                var transformedInput = inputValue.replace(/[^a-zA-Z' ]/g, '');
                if (transformedInput != inputValue) {
                    modelCtrl.$setViewValue(transformedInput);
                    modelCtrl.$render();
                }
                return transformedInput;
            });
        }
    };
});

