/**
  * Browser deprecation warning.
  *
  * Makes use of [Bowser](https://github.com/ded/bowser), see for strings to
  * use to nominate browsers for deprecation warnings.
  *
  * Basic usage
  *
  * // In your JavaScript, if you are not using the kitchen sink rollup, include
  * // the module:
  * angular.module('app', ['fe-deprecate-browsers']);
  *
  * // In your HTML, include the following template. Attributes/options match
  * // default values and can be left off if you want the default values.
  * <fe-deprecate-browsers fe-deprecate-browsers="msie:8,msie:9"></fe-deprecate-browsers>
  */
angular.module('fe-deprecate-browsers', ['xdc-storage'])
    .value('feBrowser', (function() {
        if (!window.bowser) {
            console.warn('feBrowser: "bowser" library is missing! Make sure you have a right build.');
        }
        return window.bowser || {};
    }()))
    .directive('feDeprecateBrowsers', function(lightbox, feBrowser, localStore) {
        var STORAGE_KEY = 'feDeprecateBrowsers-last-check';

        return {
            scope: {
                deprecated: '@feDeprecateBrowsers'
            },
            link: function(scope, el, attrs) {
                scope.deprecated = scope.deprecated || 'msie:8,msie:9';

                if (localStore.get(STORAGE_KEY) === scope.deprecated) {
                    return;
                }

                var currentVersion = Number(feBrowser.version);
                var isDeprecated = scope.deprecated
                    .split(',')
                    .reduce(function(isDeprecated, browser) {
                        browser = browser.trim();
                        if (!browser) {
                            return isDeprecated;
                        }
                        browser = browser.split(':');
                        var name = browser[0].trim();
                        var version = Number(browser[1].trim());
                        return isDeprecated || (!!feBrowser[name] && (isNaN(version) || currentVersion <= version));
                    }, false);

                if (isDeprecated) {
                    lightbox({
                        title: 'Upgrade Your Browser',
                        css: {
                            width: '600px',
                            height: '300px'
                        },
                        templateContent: 'fe-shared-components/templates/deprecate-browsers/dialog.tpl.html',
                        parentScope: scope,
                        callback: {
                            close: function() {
                                localStore.set(STORAGE_KEY, scope.deprecated);
                            }
                        }
                    }).open();
                }
            }
        };
    });

