/**
 * Validate emails according to standard auction/ten-x design guidelines.
 *
 * Can be used on Angular input[type=email] and will prefer to override the
 * in built validation.
 */
angular.module('fe-validators')
.directive('feValidEmail', function() {
    var EMAIL_REGEXP = /^[a-z0-9][a-z0-9_\.\-]*@[a-z0-9\.\-]+\.[a-z]{2,4}$/i;

    return {
        restrict: 'AE',
        require: 'ngModel',
        link: function(scope, elm, attrs, ctrl) {
            if (ctrl) {

                var validateEmail = function validateEmail(modelValue, viewValue) {
                    return ctrl.$isEmpty(modelValue) || EMAIL_REGEXP.test(viewValue);
                };

                if (ctrl.$validators) { // Angular 1.3+
                    var vname = ctrl.$validators.email ? 'email' : 'feValidEmail';
                    ctrl.$validators[vname] = validateEmail;
                } else { // Angular 1.2
                    ctrl.$parsers.push(function(viewValue) {
                        ctrl.$setValidity('email', validateEmail(viewValue, viewValue));
                        return viewValue;
                    });
                }
            }
        }
    };
});


