angular.module('fe-live-agent')
    .service('feLiveAgentSvc', function(
        $window,
        $q,
        lightbox,
        FE_LIVE_AGENT_CONSTANTS
    ) {

        // salesforce, provides only shortcats to toggle element visibility
        // no API to read the status (let me know if you find one)

        var isOnline = false;

        var onlineStub = document.createElement('div');
        onlineStub.style.display = 'none';

        var offlineStub = document.createElement('div');
        offlineStub.style.display = 'none';

        $window._laq = $window._laq || [];
        $window._laq.push(function() {
            $window.liveagent.showWhenOnline(FE_LIVE_AGENT_CONSTANTS.clientId, onlineStub);
            $window.liveagent.showWhenOffline(FE_LIVE_AGENT_CONSTANTS.clientId, offlineStub);
            // Can't make this work
            // $window.liveagent.setChatWindowHeight(350);
            // $window.liveagent.setChatWindowWidth(480);
        });


        function checkStatus(done) {
            $window.liveagent.init(
                FE_LIVE_AGENT_CONSTANTS.initUrl,
                FE_LIVE_AGENT_CONSTANTS.buttonId,
                FE_LIVE_AGENT_CONSTANTS.orgId
            );

            var checker = setInterval(function() {
                if (onlineStub.style.display !== 'none') {
                    done(true);
                    clearInterval(checker);
                } else if (offlineStub.style.display !== 'none') {
                    done(false);
                    clearInterval(checker);
                }
            }, 50);
        }

        // Assures script is loaded on page and
        // does initial online status check
        //
        // @return Promise resolved with `liveagent` object
        function init() {

            var result = $q.defer();

            function resolve() {
                checkStatus(function(status) {
                    isOnline = status;
                    result.resolve($window.liveagent);
                });
            }

            // if script is already on page
            if ($window.liveagent) {
                resolve();

            // load script
            } else {
                // bring script to the page
                var script   = document.createElement('script');
                script.type  = 'text/javascript';
                script.async = true;
                script.src   = FE_LIVE_AGENT_CONSTANTS.scriptUrl;

                // run setups and callbacks when loaded
                script.onload = script.onreadystatechange = function() {
                    if (
                        !this.readyState ||
                        this.readyState === 'loaded' ||
                        this.readyState === 'complete'
                    ) {
                        resolve();
                    }
                };

                // load script
                var s = document.querySelector('script');
                s.parentNode.insertBefore(script, s);
            }

            // resolve eventualy
            return result.promise;
        }

        // Opens a lightbox with start form
        function open() {
            init().then(function(liveagent) {
                if (isOnline) {
                    liveagent.startChat(FE_LIVE_AGENT_CONSTANTS.clientId);
                } else {
                    var win = $window.open(
                        FE_LIVE_AGENT_CONSTANTS.offlineUrl,
                        'liveagent',
                        'width=400,height=500,menubar=no,location=no,resizable=no,status=no'
                    );

                    if (win.focus) {
                        win.focus();
                    }
                }
            });
        }

        return {
            open: open
        };

    });

