/**
 * Service returning a promise resolved with suggestion results
 * Rsults may be fullfilled localy (stae, zip, etc) or with
 * remote call (feSearchSuggestSvc(q))
 *
 * Returns first 9 items ant appends text item
 *
 * @TODO unit tests
 */
angular.module('fe-search')
    .factory('feSearchGetSuggestionsSvc', function(
        FE_STATES,
        feSearchSuggestSvc,
        $q
    ) {

        function getState(value) {
            return FE_STATES[(value + '').toUpperCase()];
        }

        function isState(value) {
            return !!getState(value);
        }

        function isZip(value) {
            return /^\d{5}$/.test(value);
        }

        return function getSuggestions(q) {

            var result = null;

            if (q.length < 2) {
                result = [];
            } else if (isState(q)) {
                result = [{
                    type  : 'state',
                    state : q,
                    value : getState(q)
                }];
            } else if (isZip(q)) {
                result = [{
                    type  : 'zip',
                    zip   : q,
                    value : q
                }];
            }

            return (result ? $q.when(result) : feSearchSuggestSvc(q))
                .then(function(items) {
                    return items
                        .map(function(item) {
                            return {
                                label    : item.value,
                                subLabel : item.type,
                                value    : item
                            };
                        })
                        .slice(0, 9)
                        .concat({
                            label    : q,
                            subLabel : 'text',
                            value    : {
                                type  : 'text',
                                text  : q,
                                value : q
                            }
                        });
                });
        };
    });


/**
 * Takes suggested location (autosuggest model) and
 * returns parameters set for actual search. Returned object
 * shoulld follow this structure
 *
 *     {
 *          search_text   : '',
 *          search_zip    : '',
 *          search_county : '',
 *          search_city   : '',
 *          search_state  : ''
 *     }
 *
 * @TODO unit tests
 */

angular.module('fe-search')
    .factory('feSearchParamsSvc', function(FE_STATES) {

        return function searchParams(suggestion) {
            var params = {
                search_text   : '',
                search_zip    : '',
                search_county : '',
                search_city   : '',
                search_state  : ''
            };

            if (
                !suggestion ||
                !suggestion.value ||
                !suggestion.value.type
            ) {
                if (suggestion && suggestion.label){ params.search_text = suggestion.label; }
                return params;
            }


            switch (suggestion.value.type) {
                case 'city':
                    params.search_city = suggestion.value.city.trim();
                    params.search_state = suggestion.value.state.trim();
                    params.auto_flag = 'yes';
                    break;
                case 'zip':
                    params.search_zip = suggestion.value.zip;
                    params.auto_flag = 'yes';
                    break;
                case 'county':
                    params.search_county = suggestion.value.county.split(/\scounty/i)[0];
                    params.search_state = suggestion.value.state.trim();
                    params.auto_flag = 'yes';
                    break;
                case 'state':
                    params.search_state = suggestion.value.state.trim();
                    params.auto_flag = 'yes';
                    break;
                case 'text':
                    params.search_text = suggestion.value.text.trim();
            }

            return params;
        };
    });

