angular.module('fe-snapEngage')
    .factory('feSnapEngageSvc', function(FE_SNAPENGAGE_CONSTANTS, $window) {

        var deployed = false;

        function assureDeployed(done) {

            // if somehow SnapEngage is already there
            // assume it's deployed
            if ($window.SnapEngage) {
                deployed = true;
            }

            // if deployed run the callback asynchronously to keep
            // same flow as it was script.onload
            if (deployed) {
                return setTimeout(done, 0);
            }

            // setup SnapEngage script
            var se = document.createElement('script');
            se.type = 'text/javascript';
            se.async = true;
            se.src = FE_SNAPENGAGE_CONSTANTS.scriptUrl;

            // run setups and callbacks when loaded
            se.onload = se.onreadystatechange = function() {
                if (!deployed && (!this.readyState || this.readyState === 'loaded' || this.readyState === 'complete')) {
                    deployed = true;
                    FE_SNAPENGAGE_CONSTANTS.onLoad();
                    done();
                }
            };

            // load script
            var s = document.getElementsByTagName('script')[0];
            s.parentNode.insertBefore(se, s);
        }

        return {
            startLink: function() {
                assureDeployed(function() {
                    $window.SnapEngage.startLink();
                });
            }
        };
    });

