angular.module('fe-user')
    .factory('feUserSvc', function(dataBootstrap, environmentSvc, $http, $log, $q, SHARED_CONSTANTS) {
        // 2016 Feb 5: The added details.loggedIn property is used in other code
        // that references the details directly. For now, keep the loggedIn property
        // available by modifying the details when they are returned.
        //
        // Takes a raw account details JavaScript literal and returns an app
        // friendly/modified version.
        var accountDetailsFactory = function(details) {
            // We are only logged in if there is an email_address. If not, we
            // are not logged in. Historical choice to allow this function to be
            // used in a couple of spots.
            details = details || {};
            if (details.email_address) {
                // Give other apps what they want for now.
                details.loggedIn = true;
            }
            return details;
        };


        var feUser = {
            // 2016 Jan 28: Historically, we have added user details (aka the response
            // from an account/details request) JSON to the page. The existence
            // of this JSON has often meant the user is logged, and the absence means
            // the user is not logged in. Instead of making an HTTP request, respect
            // the data bootstrap (aka. window.ADC object in the page) and pull
            // our user details from it.
            details: accountDetailsFactory(dataBootstrap.get('model.user') || dataBootstrap.get('user')),

            // Attempts to load the user details with existing credentials.
            // Returns the modified $http promise that itself will resolve with the
            // account details of the user or will reject with an empty object.
            setUser: function() {
                return $http.post(environmentSvc.normalizeUrlByDomain(SHARED_CONSTANTS.USER_DETAILS), {})
                    .then(function(response) {
                        feUser.details = accountDetailsFactory(response.data);
                        return $q.when(feUser.details);
                    })['catch'](function(response) {
                        feUser.details = accountDetailsFactory();
                        return $q.reject(feUser.details);
                    });
            },

            /**
             * @deprecated Please do not use. There are other functions that will
             * do what you want.
             * @return {$q Promise} Returns the $http promise.
             */
            isUserLoggedIn: function() {
                $log.warn('isUserLoggedIn() is deprecated. Please use isLoggedIn or another method.');
                return $http({
                    url: environmentSvc.normalizeUrlByDomain(SHARED_CONSTANTS.USER_DETAILS),
                    method: 'POST'
                });
            },

            /**
             * Synchronous request to detect are we logged in right now according
             * to the information we have at hand?
             * @return {Boolean} True or false, based on what we know right now.
             */
            isLoggedIn: function() {
                // We do this as best guess to work with raw platform API data.
                return !!this.details.email_address;
            },

            login: function(email, password, app) {
                return $http({
                    url: environmentSvc.isTenXLocalDev() ? environmentSvc.normalizeUrlByDomain(SHARED_CONSTANTS.USER_LOGIN) : SHARED_CONSTANTS.USER_LOGIN.url,
                    method: 'POST',
                    data: {
                        'email': email,
                        'password': password,
                        'app_name': app
                    },
                    headers: {
                        'Content-Type': 'application/json'
                    }
                });
            },

            createAccount: function(newUserObj) {
                return $http.post(environmentSvc.normalizeUrlByDomain(SHARED_CONSTANTS.CREATE_ACCOUNT), {
                    new_user_data: newUserObj
                });
            },

            suggestEmail: function(emailVal) {
                var suggestedEmailDomain = {
                    'gmail.com': ['gmial', 'gmai', 'gamil', 'gma'],
                    'yahoo.com': ['ya', 'yah', 'yaho'],
                    'hotmail.com': ['hotmal', 'hotmai', 'hotmil', 'homail']
                };
                // Assume underscore is included in parent repo
                return _.findKey(suggestedEmailDomain, function(o) {
                    return _.indexOf(o, emailVal) >= 0;
                });
            },

            logout: function() {
                //TODO: logout
            }
        };

        return feUser;

    });

